mapper.select:
select *
from tb_server_device_fault
where device_type in

  #{item}


  and add_time > #{startTime}


  and  add_time = #{endTime}

select count(*)
from tb_server_device_fault
where
device_type in

  #{item}


  and add_time > #{startTime}


   and add_time < #{endTime} 


  and `type` = #{faultType}


  and status = #{status}


  and is_outdoor = #{isOutdoor}


  and device_code like concat('%', #{deviceCode}, '%');

select *
from tb_server_device_fault
where
device_type in

  #{item}


  and add_time > #{startTime}


   and  add_time < #{endTime} 


  and fault_type = #{faultType}


  and status = #{status}


  and is_outdoor = #{isOutdoor}


  and device_code like concat(%, #{deviceCode}, %);


  limit #{currentPage}, #{limit}

order by id DESC
