#{item}
UNION ALL select count(*) from tb_server_alarm where status != 0
AND device_type IN
#{item}

select *
from tb_server_alarm
where add_time > #{startTime}

   and add_time < #{endTime} 


  and  device_type IN

    #{item}

select count(*)
from tb_server_alarm
where 1=1

 and device_type in

  #{item}



  and add_time > #{startTime}


   and add_time < #{endTime} 


  and `type` = #{alarmType}


  and status = #{status}


  and is_outdoor = #{isOutdoor}


  and device_code like concat('%', #{deviceCode}, '%')

select *
from tb_server_alarm
where
device_type in

  #{item}


  and add_time >= #{startTime}


   and add_time < #{endTime} 


  and `type` = #{alarmType}


  and status = #{status}


  and is_outdoor = #{isOutdoor}


  and device_code like concat('%', #{deviceCode}, '%')

order by id DESC

  limit #{currentPage}, #{length}

select id from tb_server_alarm where id in
#{item}
