mapper.delete:
delete from tb_server_alarm
where id IN

  #{item}

mapper.update:
update tb_server_alarm
`count` = `count`+1, update_time = unix_timestamp(now())
where id = #{id}

mapper.select:
SELECT count(*) as amount, status from tb_server_alarm
where is_delete = 0

  AND device_type in

  #{item}



  and add_time >= #{startTime}


   and add_time < #{endTime} 


  and companyId = #{companyId}

group by status

SELECT count(*) as amount, `type` as alarmType from tb_server_alarm
where is_delete = 0

  and add_time >= #{startTime}


   and add_time < #{endTime} 


  and companyId = #{companyId}

group by `type`
order by amount DESC
limit 5

SELECT count(*) as amount, status from tb_server_alarm
where add_time >= #{startTime}

   and add_time < #{endTime} 


    and company_id = #{companyId}

GROUP BY status

SELECT count(*) as amount, `type` as alarmType from tb_server_alarm
where is_delete = 0

AND
device_type in

  #{item}



  and add_time >= #{startTime}


   and add_time < #{endTime} 


  and companyId = #{companyId}

group by `type`

SELECT deal_user_id as dealUserId, count(*) as amount from tb_server_alarm
where is_delete = 0

and
device_type in

  #{item}



  and deal_time >= #{startTime}


   and deal_time < #{endTime} 


  and companyId = #{companyId}

GROUP BY deal_user_id order by amount desc

